import React, { useCallback, useState } from "react";
import { BiSolidUserCircle } from "react-icons/bi";

import "../components_styles/Playlist.css";

import Tracklist from "./Tracklist";

function Playlist(props) {
  const handleNameChange = useCallback(
    (event) => {
      props.onNameChange(event.target.value);
    },
    [props.onNameChange]
  );

  const [user, setUser] = useState("user name");
  // tady nějak provázat data přihlášeného uživatele -->nene, provazovat budu v app

  // vytvořit podmíněnost obrázku - jsem přihlášený & uživatel má img? použít to, jinak použít <BiSolidUserCircle/>

  return (
    <div className="Playlist">
      <div className="Playlist-headlines">
        <h2>My Playlist</h2>
        <input onChange={handleNameChange} defaultValue={"New Playlist"} />
      </div>
      <div className="User">
        <BiSolidUserCircle />
        <p className="User" user={props.userName}></p>
      </div>

      <hr />
      <Tracklist
        tracks={props.playlistTracks}
        isRemoval={true}
        onRemove={props.onRemove}
      />
      <button className="PlaylistSave" onClick={props.onSave}>
        Add Playlist to Spotify
      </button>
    </div>
  );
}

export default Playlist;
