import React, { useCallback } from "react";
import { BiSolidUserCircle } from "react-icons/bi";

import "../components_styles/UserData.css";


function UserData(props) {
   const handleLogin = useCallback(
      (event) => {
         props.onLogin(event.target.value);
      }, [props.onLogin]
   );

 return (
    <div className="User" onLogin={handleLogin}>
        <img 
        src={props.userImg ? props.userImg : BiSolidUserCircle } 
        alt="img"
        /> 
        <p>{props.userName}</p>
    </div>
 )
}

export default UserData;



const getUser = useCallback(() => {
   // tady tahám data uživatele a ustanovuji jeho přihlášené jméno
   Spotify.getUser().then(() => {
     setUserName();
     setUserImg();
   })
 })

function App(){
 const [userName, setUserName] = useState("user name");
  const [userImg, setUserImg] = useState("profile Img");

 return (
   <div className='App'>
     <div className='ColumnsContainer'>
        <Playlist 
         className='Playlist' 
         playlistName={playlistName}
         playlistTracks={playlistTracks}
         onNameChange={updatePlaylistName}
         onRemove={removeTrack}
         onSave={savePlaylist}
         onLogin={getUser}
         userName={userName}
         userImg={userImg}
       />
     </div> 
   </div>
 );
};
